import React from 'react';

const SCORE_URL = 'http://cgi.cse.unsw.edu.au/~cs6080/21T1/data/score.json';
const Home = () => {
  const [loadingState, setLoadingState] = React.useState('idle');
  const [score,setScore] = React.useState(undefined);
  const [reset, setReset] = React.useState(false);
  React.useEffect(() => {
    async function fetchCatFacts() {
      setLoadingState('loading');
      const response = await fetch(SCORE_URL);
      const json = await response.json();
      setScore(json['score']);
      setLoadingState('success');
      setReset(false);
    }
    fetchCatFacts();
  }, [reset]);
  if(score === 0 && !reset){
    alert('Congratulations');
    setReset(true);
  }
  return (
    <div className="mainpage">
      {loadingState !== 'success' && <p>Loading...</p>}
      {loadingState === 'success' && (
        <div>
          <p className="dashboardFirstLine">
            Please choose an option from the sidebar.
          </p>
          <p className="dashboardFirstLine">Games left to win: {score}</p>
          <button onClick={()=>{setReset(true)}}>Reset</button>
          <button onClick={()=>{setScore(x=>x-1)}}>-</button>
        </div>
      )}
    </div>
  );
};
export default Home;
